        [TestMethod]
        public void TestMethod1()
        {
            string example = "(())";
            int solved = Solve(example);
            Assert.AreEqual(0, solved);
        }

        [TestMethod]
        public void TestMethod2()
        {
            string example = "(((";
            int solved = Solve(example);
            Assert.AreEqual(3, solved);
        }

        [TestMethod]
        public void TestMethod3()
        {
            string example = "(()(()(";
            int solved = Solve(example);
            Assert.AreEqual(3, solved);
        }

        [TestMethod]
        public void TestMethod4()
        {
            string example = ")";
            int solved = Solve2(example);
            Assert.AreEqual(1, solved);
        }  
        
        [TestMethod]
        public void TestMethod5()
        {
            string example = "()())";
            int solved = Solve2(example);
            Assert.AreEqual(5, solved);
        }

        private static readonly Regex openRegex = new Regex("\\(", RegexOptions.Compiled, TimeSpan.FromSeconds(5));
        private static readonly Regex closeRegex = new Regex("\\)", RegexOptions.Compiled, TimeSpan.FromSeconds(5));

        private int Solve(string input)
        {
            int open = openRegex.Matches(input).Count;
            int close = closeRegex.Matches(input).Count;
            return open - close;
        }

        private int Solve2(string input)
        {
           
            int currentFloor = 0;

            for (int i = 0; i < input.Length; i++)
            {
                if (input[i] == '(')
                    currentFloor++;
                else
                    currentFloor--;

                if (currentFloor < 0)
                {
                    return i + 1;
                }

            }

            return -1;
        }
    }
